mapper.select:
select count(*)
from blist
where f_status='Y' and category=#{cate}

select count(*)
from blist
where f_status='Y' and category=#{cate} and 

	price>=#{sc.startPrice} and price<=#{sc.endPrice}


	and ad1=#{ad1} and ad2=#{ad2}

select count(*)
from heart
where user_id=#{uid} and fno2=#{fno}

select DISTINCT AD1, AD2
from BLIST
where category=#{cate}

mapper.update:
update board
set heart = heart + 1
where fno=#{fno}

update board
set heart = heart - 1
where fno=#{fno}
